    private void makeHistButtonActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_makeHistButtonActionPerformed
    {//GEN-HEADEREND:event_makeHistButtonActionPerformed
        ta = new TextAnalyzer(inputTextArea.getText());
        histogram.append("Word" + "\t\tFrequency\t\u25A0 = 10\n\n");
        while(ta.hasNextHistEntry())
        {
            histogram.append(ta.nextHistEntry() + "\n");
        }
    }//GEN-LAST:event_makeHistButtonActionPerformed

